mapper.insert:
INSERT INTO board (
					bno
				   ,title
				   ,writer
				   ,content
				   ,regdate
				   ,viewcnt
				  )
	 VALUES (
	 		 board_seq.nextVal
	 		,#{title}
	 		,#{writer}
	 		,#{content}
	 		,SYSDATE
	 		,0
	 		)

mapper.update:
UPDATE board
SET
	title        = #{title}
	,content     = #{content}
	,updatedate  = SYSDATE
WHERE bno 		 = #{bno}
